mapper.select:
SELECT t1.ID, t1.`CODE`, t1.`NAME`, t1.TYPE, t1.DES, t1.PARENT_ID, t1.ICON FROM BI_UI_ANALYSE_CATEGORY t1 LEFT JOIN ( SELECT t2.RESOURCE_ID FROM BI_UI_ANALYSE_USER_RESOURCE t2 LEFT JOIN ( SELECT t3.RESOURCE_ID FROM BI_UI_ANALYSE_USER_RESOURCE t3 WHERE t3.USER_ID = #{queryDto.userId,jdbcType=VARCHAR} AND t3.RESOURCE_TYPE = #{queryDto.resourceType,jdbcType=VARCHAR} AND LOCATE( #{queryDto.permittedAction,jdbcType=VARCHAR}, t3.PERMITTED_ACTION ) > 0 GROUP BY t3.RESOURCE_ID ) t4 ON t2.RESOURCE_ID = t4.RESOURCE_ID WHERE t4.RESOURCE_ID IS NULL GROUP BY t2.RESOURCE_ID ) t5 ON t1.ID = t5.RESOURCE_ID WHERE t5.RESOURCE_ID IS NULL
AND t1.`NAME` LIKE CONCAT('%',#{queryDto.name,jdbcType=VARCHAR},'%')
AND t1.TYPE = #{queryDto.type,jdbcType=VARCHAR}
AND t1.TENANT_ID = #{queryDto.tenantId,jdbcType=VARCHAR}
