mapper.select:
SELECT t1.ID, t1.`CODE`, t1.`NAME`, t1.DES, t1.TYPE, t1.ICON, t1.CATEGORY_ID, t1.EDIT_ID, t1.PUBLISH_ID, t1.IS_PUBLIC, t1.HAVE_NAV, t1.CREATE_USER, t1.CREATE_DATE, t1.MODIFIED_DATE, t1.MODIFIED_USER FROM BI_UI_ANALYSE_PAGE t1 LEFT JOIN ( SELECT t2.RESOURCE_ID FROM BI_UI_ANALYSE_USER_RESOURCE t2 LEFT JOIN ( SELECT t3.RESOURCE_ID FROM BI_UI_ANALYSE_USER_RESOURCE t3 WHERE t3.USER_ID = #{queryDto.userId,jdbcType=VARCHAR} AND t3.RESOURCE_TYPE = #{queryDto.resourceType,jdbcType=VARCHAR} AND LOCATE( #{queryDto.permittedAction,jdbcType=VARCHAR}, t3.PERMITTED_ACTION ) > 0 GROUP BY t3.RESOURCE_ID ) t4 ON t2.RESOURCE_ID = t4.RESOURCE_ID WHERE t4.RESOURCE_ID IS NULL AND t2.RESOURCE_TYPE = #{queryDto.resourceType,jdbcType=VARCHAR} GROUP BY t2.RESOURCE_ID ) t5 ON t1.ID = t5.RESOURCE_ID WHERE t5.RESOURCE_ID IS NULL AND t1.PUBLISH_ID IS NOT NULL
AND t1.`NAME` LIKE concat('%', #{queryDto.name,jdbcType=VARCHAR},'%')
AND t1.CATEGORY_ID IN
#{item, jdbcType=VARCHAR}
AND t1.IS_EDIT = #{queryDto.isEdit,jdbcType=VARCHAR}
AND t1.TENANT_ID = #{queryDto.tenantId,jdbcType=VARCHAR}
ORDER BY t1.CREATE_DATE DESC
